// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract SocialPlatformTracker is Ownable {
    using Counters for Counters.Counter;

    // Enum for comprehensive user and challenge statuses
    enum ChallengeState {
        PENDING,
        AWAITING_ACCEPTANCE,
        ACTIVE,
        COMPLETED_WON,
        COMPLETED_LOST,
        DECLINED,
        CANCELLED
    }

    enum NotificationType {
        CHALLENGE_RECEIVED,
        CHALLENGE_ACCEPTED,
        CHALLENGE_DECLINED,
        CHALLENGE_COMPLETED,
        CHALLENGE_EXPIRED,
        REWARD_RECEIVED,
        PROFILE_MILESTONE
    }

    // Leaderboard Structures
    struct LeaderboardEntry {
        address user;
        uint256 totalChallenges;
        uint256 wonChallenges;
        uint256 lostChallenges;
        uint256 reputation;
        uint256 totalEarnings;
    }

    // Notification Structure
    struct Notification {
        uint256 id;
        address recipient;
        NotificationType notificationType;
        uint256 timestamp;
        uint256 challengeId;
        uint256 amount;
        bool isRead;
    }

    // Activity Tracking Structure
    struct ActivityLog {
        uint256 id;
        address user;
        string activityType;
        uint256 timestamp;
        uint256 challengeId;
        uint256 amount;
    }

    // Leaderboard Management
    mapping(address => LeaderboardEntry) public leaderboardEntries;
    address[] public leaderboardUsers;

    // Notification System
    mapping(address => Notification[]) public userNotifications;
    Counters.Counter private notificationIdCounter;

    // Activity Logging
    mapping(address => ActivityLog[]) public userActivities;
    Counters.Counter private activityLogIdCounter;

    // Challenge Tracking
    mapping(address => uint256[]) public userChallengesByState;
    mapping(address => mapping(ChallengeState => uint256[])) public categorizedChallenges;

    // Events
    event LeaderboardUpdated(address indexed user, uint256 reputation, uint256 totalEarnings);
    event NotificationCreated(address indexed recipient, NotificationType indexed notificationType);
    event ActivityLogged(address indexed user, string activityType, uint256 timestamp);

    // Leaderboard Functions
    function updateLeaderboard(
        address _user, 
        bool _won, 
        uint256 _amount
    ) external {
        LeaderboardEntry storage entry = leaderboardEntries[_user];

        // Increment challenge count
        entry.totalChallenges++;

        // Update wins/losses
        if (_won) {
            entry.wonChallenges++;
            entry.reputation += 10;
            entry.totalEarnings += _amount;
        } else {
            entry.lostChallenges++;
            entry.reputation = entry.reputation > 5 ? entry.reputation - 5 : 0;
        }

        // Ensure user is in leaderboard
        if (entry.user == address(0)) {
            entry.user = _user;
            leaderboardUsers.push(_user);
        }

        emit LeaderboardUpdated(_user, entry.reputation, entry.totalEarnings);
    }

    // Get Top Leaderboard Entries
    function getTopLeaderboard(uint256 _count) external view returns (LeaderboardEntry[] memory) {
        // Sort and return top entries
        LeaderboardEntry[] memory topEntries = new LeaderboardEntry[](_count);

        // Simple bubble sort (can be optimized)
        for (uint256 i = 0; i < leaderboardUsers.length; i++) {
            for (uint256 j = 0; j < _count; j++) {
                if (leaderboardEntries[leaderboardUsers[i]].reputation > 
                    (topEntries[j].reputation)) {
                    // Shift entries and insert
                    for (uint256 k = _count - 1; k > j; k--) {
                        topEntries[k] = topEntries[k-1];
                    }
                    topEntries[j] = leaderboardEntries[leaderboardUsers[i]];
                    break;
                }
            }
        }

        return topEntries;
    }

    // Notification System
    function createNotification(
        address _recipient,
        NotificationType _type,
        uint256 _challengeId,
        uint256 _amount
    ) external {
        notificationIdCounter.increment();
        uint256 newNotificationId = notificationIdCounter.current();

        Notification memory newNotification = Notification({
            id: newNotificationId,
            recipient: _recipient,
            notificationType: _type,
            timestamp: block.timestamp,
            challengeId: _challengeId,
            amount: _amount,
            isRead: false
        });

        userNotifications[_recipient].push(newNotification);

        emit NotificationCreated(_recipient, _type);
    }

    // Mark Notifications as Read
    function markNotificationsAsRead(uint256[] memory _notificationIds) external {
        Notification[] storage notifications = userNotifications[msg.sender];

        for (uint256 i = 0; i < _notificationIds.length; i++) {
            for (uint256 j = 0; j < notifications.length; j++) {
                if (notifications[j].id == _notificationIds[i] && 
                    notifications[j].recipient == msg.sender) {
                    notifications[j].isRead = true;
                    break;
                }
            }
        }
    }

    // Activity Logging
    function logActivity(
        address _user,
        string memory _activityType,
        uint256 _challengeId,
        uint256 _amount
    ) external {
        activityLogIdCounter.increment();
        uint256 newActivityId = activityLogIdCounter.current();

        ActivityLog memory newActivity = ActivityLog({
            id: newActivityId,
            user: _user,
            activityType: _activityType,
            timestamp: block.timestamp,
            challengeId: _challengeId,
            amount: _amount
        });

        userActivities[_user].push(newActivity);

        emit ActivityLogged(_user, _activityType, block.timestamp);
    }

    // Challenge State Tracking
    function updateChallengeState(
        address _user,
        uint256 _challengeId,
        ChallengeState _newState
    ) external {
        // Remove from previous state categories
        for (uint256 i = 0; i < categorizedChallenges[_user][_newState].length; i++) {
            if (categorizedChallenges[_user][_newState][i] == _challengeId) {
                return; // Already in this state
            }
        }

        // Add to new state category
        categorizedChallenges[_user][_newState].push(_challengeId);
    }

    // View Functions for Challenge Categories
    function getChallengesByState(
        address _user, 
        ChallengeState _state
    ) external view returns (uint256[] memory) {
        return categorizedChallenges[_user][_state];
    }

    // Get User Challenge Statistics
    function getUserChallengeStats(address _user) external view returns (
        uint256 totalChallenges,
        uint256 pendingChallenges,
        uint256 activeChallenges,
        uint256 wonChallenges,
        uint256 lostChallenges,
        uint256 declinedChallenges
    ) {
        LeaderboardEntry memory entry = leaderboardEntries[_user];

        return (
            entry.totalChallenges,
            categorizedChallenges[_user][ChallengeState.PENDING].length,
            categorizedChallenges[_user][ChallengeState.ACTIVE].length,
            entry.wonChallenges,
            entry.lostChallenges,
            categorizedChallenges[_user][ChallengeState.DECLINED].length
        );
    }

    // Unread Notification Count
    function getUnreadNotificationCount(address _user) external view returns (uint256) {
        Notification[] memory notifications = userNotifications[_user];
        uint256 unreadCount = 0;

        for (uint256 i = 0; i < notifications.length; i++) {
            if (!notifications[i].isRead) {
                unreadCount++;
            }
        }

        return unreadCount;
    }
}

contracts/SocialBetting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract SocialBetting is ReentrancyGuard, Pausable, Ownable {
    using Counters for Counters.Counter;

    // Enums
    enum ChallengeType {
        DIRECT_PREDICTION,
        THIRD_PARTY_VERIFIED,
        COMMUNITY_VOTE,
        TIME_BASED
    }

    enum ChallengeStatus {
        PENDING,
        ACCEPTED,
        COMPLETED,
        SETTLED
    }

    // Structs
    struct Challenge {
        uint256 id;
        address challenger;
        address target;
        string prediction;
        uint256 wagerAmount;
        uint256 totalStake;
        address wagerToken;
        uint256 startTime;
        uint256 endTime;
        ChallengeType challengeType;
        ChallengeStatus status;
        uint256 communityVotingPeriod;
        uint256 communityVotes;
        mapping(address => bool) communityVoters;
        uint256 votingThreshold;
        address arbiter;
        address winner;
    }

    struct Bet {
        address creator;
        address acceptor;
        uint256 stake;
        uint256 totalStake;
        uint256 createdAt;
        uint256 duration;
        string description;
        bytes32 eventId;
        bool settled;
        address winner;
    }

    // State variables
    Counters.Counter private _challengeCounter;
    Counters.Counter private _betCounter;
    mapping(uint256 => Challenge) private challenges;
    mapping(uint256 => Bet) public bets;
    mapping(bytes32 => bool) public resolvedEvents;

    // Events
    event ChallengeCreated(
        uint256 indexed challengeId,
        address indexed challenger,
        uint256 wagerAmount,
        string prediction,
        uint256 startTime,
        uint256 endTime
    );
    event ChallengeModified(
        uint256 indexed challengeId,
        string newPrediction,
        uint256 newWagerAmount
    );
    event ChallengeAccepted(uint256 indexed challengeId, address indexed accepter);
    event ArbiterAssigned(uint256 indexed challengeId, address indexed arbiter);
    event CommunityVoteCast(
        uint256 indexed challengeId,
        address indexed voter,
        bool vote
    );
    event ChallengeResolved(
        uint256 indexed challengeId,
        address winner,
        uint256 payout
    );
    event BetCreated(
        uint256 indexed betId,
        address indexed creator,
        uint256 stake,
        string description,
        uint256 duration
    );
    event BetAccepted(
        uint256 indexed betId,
        address indexed acceptor,
        uint256 stake
    );
    event BetResolved(
        uint256 indexed betId,
        address winner,
        uint256 payout
    );

    // Modifiers
    modifier onlyBetParticipants(uint256 betId) {
        require(
            msg.sender == bets[betId].creator || msg.sender == bets[betId].acceptor,
            "Not a participant in this bet"
        );
        _;
    }

    // Functions

    // Create a new challenge
    function createChallenge(
        address _target,
        string memory _prediction,
        uint256 _startTime,
        uint256 _endTime,
        address _wagerToken,
        uint256 _wagerAmount,
        ChallengeType _challengeType,
        address _arbiter,
        uint256 _votingThreshold
    ) external payable whenNotPaused nonReentrant {
        require(_startTime > block.timestamp, "Start time must be in the future");
        require(_endTime > _startTime, "End time must be after start time");

        _challengeCounter.increment();
        uint256 challengeId = _challengeCounter.current();

        Challenge storage newChallenge = challenges[challengeId];
        newChallenge.id = challengeId;
        newChallenge.challenger = msg.sender;
        newChallenge.target = _target;
        newChallenge.prediction = _prediction;
        newChallenge.wagerAmount = _wagerAmount;
        newChallenge.totalStake = _wagerAmount;
        newChallenge.wagerToken = _wagerToken;
        newChallenge.startTime = _startTime;
        newChallenge.endTime = _endTime;
        newChallenge.challengeType = _challengeType;
        newChallenge.status = ChallengeStatus.PENDING;

        if (_challengeType == ChallengeType.COMMUNITY_VOTE) {
            require(_votingThreshold > 0 && _votingThreshold <= 100, "Invalid voting threshold");
            newChallenge.votingThreshold = _votingThreshold;
            newChallenge.communityVotingPeriod = _endTime + 1 days;
        }

        if (_challengeType == ChallengeType.THIRD_PARTY_VERIFIED) {
            require(_arbiter != address(0), "Arbiter required for this type");
            newChallenge.arbiter = _arbiter;
            emit ArbiterAssigned(challengeId, _arbiter);
        }

        emit ChallengeCreated(
            challengeId,
            msg.sender,
            _wagerAmount,
            _prediction,
            _startTime,
            _endTime
        );
    }

    // Modify a challenge
    function modifyChallenge(
        uint256 _challengeId,
        string memory _newPrediction,
        uint256 _newWagerAmount
    ) external nonReentrant {
        Challenge storage challenge = challenges[_challengeId];

        require(msg.sender == challenge.challenger, "Only challenger can modify");
        require(challenge.status == ChallengeStatus.PENDING, "Cannot modify accepted challenge");

        if (bytes(_newPrediction).length > 0) {
            challenge.prediction = _newPrediction;
        }

        emit ChallengeModified(_challengeId, _newPrediction, _newWagerAmount);
    }

    // Update wager amount
    function updateWager(uint256 _challengeId, uint256 _newWagerAmount) external payable {
        Challenge storage challenge = challenges[_challengeId]; // Ensure challenge is referenced correctly
        if (_newWagerAmount > challenge.wagerAmount) {
            uint256 additionalAmount = _newWagerAmount - challenge.wagerAmount;
            require(msg.value >= additionalAmount, "Insufficient funds");
            challenge.wagerAmount = _newWagerAmount;
        }
    }

    // Create a new bet
    function createBet(
        uint256 stake,
        uint256 duration,
        string memory description,
        bytes32 eventId
    ) external payable nonReentrant {
        require(msg.value == stake, "Stake amount must match sent value");
        require(duration > 0, "Duration must be greater than 0");

        _betCounter.increment();
        uint256 betId = _betCounter.current();

        bets[betId] = Bet({
            creator: msg.sender,
            acceptor: address(0),
            stake: stake,
            totalStake: stake,
            createdAt: block.timestamp,
            duration: duration,
            description: description,
            eventId: eventId,
            settled: false,
            winner: address(0)
        });

        emit BetCreated(betId, msg.sender, stake, description, duration);
    }

    // Accept a bet
    function acceptBet(uint256 betId) external payable nonReentrant {
        Bet storage bet = bets[betId];

        require(bet.creator != address(0), "Bet does not exist");
        require(bet.acceptor == address(0), "Bet already accepted");
        require(!bet.settled, "Bet already settled");
        require(msg.value == bet.stake, "Stake amount must match");

        bet.acceptor = msg.sender;
        bet.totalStake += msg.value;

        emit BetAccepted(betId, msg.sender, msg.value);
    }

    // Resolve a bet
    function resolveBet(uint256 betId, address winner) external onlyOwner nonReentrant {
        Bet storage bet = bets[betId];

        require(bet.creator != address(0), "Bet does not exist");
        require(bet.acceptor != address(0), "Bet not yet accepted");
        require(!bet.settled, "Bet already settled");
        require(block.timestamp >= bet.createdAt + bet.duration, "Duration not ended");
        require(winner == bet.creator || winner == bet.acceptor, "Winner must be a participant");

        uint256 totalStake = bet.totalStake;
        bet.settled = true;
        bet.winner = winner;
        resolvedEvents[bet.eventId] = true;

        payable(winner).transfer(totalStake);

        emit BetResolved(betId, winner, totalStake);
    }
}

